const sections=document.querySelectorAll("section");let currentSectionIndex=localStorage.getItem("currentSectionIndex");function scrollToSection(index){sections[index].scrollIntoView({behavior:"smooth"})}window.addEventListener("wheel",event=>{event.deltaY>0?(currentSectionIndex=Math.min(currentSectionIndex+1,sections.length-1),scrollToSection(currentSectionIndex)):event.deltaY<0&&(currentSectionIndex=Math.max(currentSectionIndex-1,0),scrollToSection(currentSectionIndex)),localStorage.setItem("currentSectionIndex",currentSectionIndex)});